// src/components/MangaList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const MangaList = () => {
  const [mangaList, setMangaList] = useState([]);

  useEffect(() => {
    // Fetch the list of manga names
    axios.get('https://34.173.150.41:3003/api/manga/')
      .then(response => {
        setMangaList(response.data);
      })
      .catch(error => {
        console.error('Error fetching manga list:', error);
      });
  }, []);

  return (
    <div>
      <h1>Manga List</h1>
      <div className="manga-covers">
        {mangaList.map(manga => (
          <Link to={`/manga/${manga.name}`} key={manga.name}>
            <img src={`https://34.173.150.41:3003/api/cover/${manga.name}.jpg`} alt={manga.name} />
          </Link>
        ))}
      </div>
    </div>
  );
};

export default MangaList;
