// src/components/MangaList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const MangaList = () => {
  const [mangaList, setMangaList] = useState([]);

  useEffect(() => {
    // Fetch the list of manga names
    axios.get('https://34.173.150.41:3003/api/manga/')
      .then(response => {
        setMangaList(response.data);
      })
      .catch(error => {
        console.error('Error fetching manga list:', error);
      });
  }, []);

  return (
    <div>
      <h1>Manga List</h1>
      <div className="manga-covers">
        {mangaList.map(mangaName => (
          <MangaCover key={mangaName} mangaName={mangaName} />
        ))}
      </div>
    </div>
  );
};

const MangaCover = ({ mangaName }) => {
  const [cover, setCover] = useState('');

  useEffect(() => {
    // Fetch manga cover based on the manga name
    axios.get(`https://34.173.150.41:3003/api/cover/${mangaName}`)
      .then(response => {
        setCover(response.data);
      })
      .catch(error => {
        console.error(`Error fetching cover for ${mangaName}:`, error);
      });
  }, [mangaName]);

  return (
    <Link to={`/manga/${mangaName}`}>
      <img style={{ width: '250px', height: '375px' }} src={cover} alt={mangaName} />
    </Link>
  );
};

export default MangaList;
