// src/components/MangaDetails.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MangaDetails = ({ match }) => {
  const { name } = match.params;
  const [cover, setCover] = useState('');
  const [volumes, setVolumes] = useState([]);

  useEffect(() => {
    // Fetch manga cover based on the manga name
    axios.get(`https://34.173.150.41:3003/api/cover/${name}`)
      .then(response => {
        setCover(response.data);
      })
      .catch(error => {
        console.error(`Error fetching cover for ${name}:`, error);
      });

    // Fetch list of volumes based on the manga name
    axios.get(`https://34.173.150.41:3003/api/manga/${name}/nums`)
      .then(response => {
        setVolumes(response.data);
      })
      .catch(error => {
        console.error(`Error fetching volumes for ${name}:`, error);
      });
  }, [name]);

  return (
    <div>
      <h1>{name} Details</h1>
      <div>
        <img src={cover} alt={`${name} Cover`} />
      </div>
      <h2>Volumes</h2>
      <ul>
        {volumes.map(volume => (
          <li key={volume}>{volume}</li>
        ))}
      </ul>
    </div>
  );
};

export default MangaDetails;
