// src/components/MangaDetails.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MangaDetails = ({ match }) => {
  const { name } = match.params;
  const [covers, setCovers] = useState([]);

  useEffect(() => {
    // Fetch manga covers based on the manga name
    axios.get(`https://34.173.150.41:3003/api/cover/${name}`)
      .then(response => {
        setCovers(response.data);
      })
      .catch(error => {
        console.error('Error fetching manga covers:', error);
      });
  }, [name]);

  return (
    <div>
      <h1>{name} Covers</h1>
      <div className="manga-covers">
        {covers.map((cover, index) => (
          <img key={index} src={cover} alt={`Cover ${index + 1}`} />
        ))}
      </div>
    </div>
  );
};

export default MangaDetails;
