// src/components/ImageViewer.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ImageViewer = ({ match }) => {
  const { name, volume } = match.params;
  const [images, setImages] = useState([]);

  useEffect(() => {
    // Fetch list of image names based on the manga name and volume
    axios.get(`https://34.173.150.41:3003/api/manga/${name}/${volume}`)
      .then(response => {
        setImages(response.data);
      })
      .catch(error => {
        console.error(`Error fetching images for ${name} - Volume ${volume}:`, error);
      });
  }, [name, volume]);

  return (
    <div>
      <h1>{name} - Volume {volume} Images</h1>
      <div className="image-list">
        {images.map((imageName, index) => (
          <img key={index} src={`https://dh4zx4a36rxfp.cloudfront.net/Manga/${name}/${volume}/${imageName}`} alt={`Image ${index + 1}`} />
        ))}
      </div>
    </div>
  );
};

export default ImageViewer;
